import React from 'react'

export default function Paragraph() {
  return (
    <div className="para-component">
    <h2>Are you aware of your <span className="custom-underline">rights</span>?<br/>
    How well do you know the <span className="custom-underline">law</span>?<br/>
    Are your <span className="custom-underline">current affairs</span> up-to date?</h2>
    <p>Staying informed and aware is essential to uphold justice in society. Every citizen carries the responsibility to be vigilant and proactive in understanding their rights and legal obligations.<br/><br/>
    It is not only our responsibility, but also our duty.</p>
</div>
  )
}
